import React, { useState, useEffect, useRef } from "react";
import Jumbotron from "../components/Jumbotron";
import DeleteBtn from "../components/DeleteBtn";
import { List, ListItem } from "../components/List";
import TextBox from "../components/TextBox";
import SearchForm from "../components/SearchForm";
import { Container, Row, Col } from "./components/Grid";


import API from "../utils/API"

function Books() {
  // Initialize books as an empty array
    const [books, setBooks] = useState([]);
    const [search, setSearch] = useState([]);
    const [results , setResults] = useState([]);
    const [error , setError] = useState([]);
    const searchTitle = useRef();

    useEffect(() => {
      loadBooks();
    }, []);

    handleInputChange = event => {
     setSearch(event.target.value);
    };

    handleFormSubmit = event => {
      event.preventDefault();
      API.getBooks(search)
        .then(res => {
          if (res.data.status === "error") {
            throw new Error(res.data.message);
          }
         setResults(res.data.message);
        })
        .catch(err => setError({ error: err.message }));
    };

    function loadBooks() {
      // Add code here to get all books from the database and store them using setBooks

      API.getBooks().then((response) => {
        setBooks(response.data);
      });

    }

    function deleteBooks(id) {
      // Add code here to get all books from the database and store them using setBooks

      API.deleteBook(id).then((response) => {
        loadBooks();
      });

    }

    function saveBook() {
      // Add code here to get all books from the database and store them using setBooks
    const bookData = {
        //  title : titleRef.current.value,
        //  author : authorRef.current.value,
        //  synopsis : synopsisRef.current.value
    }
      API.saveBook(bookData).then((response) => {
        setBooks(response.data);
       alert()
      });

    }

    return (
      <Container fluid>
        <Row>
          <Col size="md-6">
            <Jumbotron>
              <h1>Search for books</h1>
            </Jumbotron>
             <form>
              <Input name="title" placeholder="Title (required)" inputarea= {searchTitle}/>

              <FormBtn onClick={() => handleFormSubmit()}>Submit Book </FormBtn>
            </form >

          </Col>
          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>Books On My List</h1>
            </Jumbotron>
            {books.length ? (
              <List>
                {books.map(book => (
                  <ListItem key={book._id}>
                    <a href={"/books/" + book._id}>
                      <strong>
                        {book.title} by {book.author}
                      </strong>
                    </a>
                    <DeleteBtn onClick = {() => deleteBooks(book._id)}/>
                  </ListItem>
                ))}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Row>
      </Container>
    );
  }

export default Books;
